#include <iostream>
using namespace std;

int main()
{
    // INput validation
    int height = 0;
    int i = 0;
    int start_location = 1;
    string abc = "";

    cout << "Enter the height: " << endl;

    cin >> height;

    // Input validation
    if (height <= 0)
    {
        cout << "Invalid input." << endl;
        return 1;
    }

    // Alogorithm to make the triangle
    for(int i = 0; i < height; i++)    
    {
        if ( i == 0)
        {
            cout << 'a' << endl;
        }
        else
        {
            cout << abc.substr(start_location, start_location + 2) << endl;
            start_location = start_location + (2 * i);
        }
    }
}
